analyst_log_ch = logging.StreamHandler()
analyst_log_ch.setLevel(logging.DEBUG)
formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s')
analyst_log_ch.setFormatter(formatter)
analyst_log.addHandler(analyst_log_ch)


def is_same_layout_or_view_group(actual, expect):
    if actual.get('class') == expect.get('class') and (
            'Layout' in actual.get('class') or 'ViewGroup' in actual.get('class')):
        return True
    elif actual.get('class') != expect.get('class') and (
            'Layout' in actual.get('class') or 'ViewGroup' in actual.get('class')) and (
            'Layout' in expect.get('class') or 'ViewGroup' in expect.get('class')):
        return True
    else:
        return False


def is_same_widget(actual, expect):
    return actual.get('class') == expect.get('class')


place_holder = 'J'

widget_attrib_map = {
    'TextView': ['text', 'enable', 'visible-to-user'],
    'EditText': ['text', 'enable', 'focusable', 'visible-to-user'],
    'Button': ['clickable', 'long-clickable', 'text', 'visible-to-user', 'enable'],
    'CheckBox': ['text', 'visible-to-user', 'checkable', 'checked', 'enable'],
    'ImageView': [place_holder],
    'ImageButton': [place_holder],
    'ProgressBar': [place_holder],
}


def get_widget_class(widget: et.Element):
    _ = widget.get('class')
    _class = _[_.rfind('.') + 1:]
    return _class


def is_same_attribute(actual: et.Element, expect: et.Element):
    # TextView
    # EditText
    # ImageView
    # RadioButton
    # Button
    # ImageButton
    # CheckBox
    # DatePicker
    # Spinner
    # ProgressBar
    _class = get_widget_class(actual)
    return reduce(lambda x, y: x and y,
                  map(lambda attribute: actual.get(attribute, None) == expect.get(attribute, None),
                      (attribute for attribute in widget_attrib_map[_class])))


def filter_attribute(widget: et.Element):
    _class = get_widget_class(widget)
    d = dict(map(lambda attribute: (attribute, widget.get(attribute, None)),
                 (attribute for attribute in widget_attrib_map[_class])))
    d['class'] = widget.get('class')
    return d


class Analyst:
    def __init__(self):
        pass

    def is_same_gui(self, actual, expect):
        actual = et.fromstring(actual)
        expect = et.fromstring(expect).find(".*[@package='" + actual.get('package') + "']")
        return self._is_same_gui(actual, expect)

    def _is_same_gui(self, actual: et.Element, expect: et.Element):
        if len(actual) == len(expect):
            if len(actual) == 0:
                if is_same_widget(actual, expect):
                    result = is_same_attribute(actual, expect)
                    if not result:
                        analyst_log.debug(filter_attribute(actual))
                        analyst_log.debug(filter_attribute(expect))
                    return result
            else:
                for i in range(len(expect)):
                    result = self._is_same_gui(actual[i], expect[i])
                    if not result:
                        return result
                return True
        else:
            analyst_log.debug('actual has child: {}, expect has child: {}'.format(len(actual), len(expect)))
            return False
