import os
from xml.etree import ElementTree as et
from model.screen2vec.get_embedding import get_embedding
import torch
import json


def bounds2list(bounds):
    s = bounds
    s = '[' + s + ']'
    s = s[:s.find(']') + 1] + ',' + s[s.find(']') + 1:]
    return eval(s)


def gui_xml2json_helper(node):
    children = []
    d_ = node.attrib
    for child in node:
        children.append(gui_xml2json_helper(child))
    d_['children'] = children
    if 'bounds' not in d_:
        d_['bounds'] = None
    else:
        bounds = bounds2list(d_['bounds'])
        d_['bounds'] = [
            bounds[0][0],
            bounds[1][0],
            bounds[0][1],
            bounds[1][1]
        ]
    return d_


def gui_xml2json(xml: et, activity_name):
    root = et.fromstring(xml)
    d = gui_xml2json_helper(root)
    result = {
        'activity_name': activity_name,
        'is_keyboard_deployed': False, 'activity': {'root': d}}
    # f = open('calendar.json', 'w+')
    # json.dump(result, f, indent=4)
    return result


def gui_similarity_matrix(record):
    records = record['d2e']
    data = dict()
    for i in range(len(records)):
        record = records[i]
        data[i] = {
            'description': record['description'],
            'pre_xml': record['pre_xml'],
            'post_xml': record['post_xml']
        }
    for i in data:
        embedding1 = get_embedding(
            screen=data[i]['pre_xml'],
            ui_model='../model/UI2Vec_model.ep120',
            screen_model='../model/Screen2Vec_model_v4.ep120',
            layout_model='../model/layout_encoder.ep800',
            num_predictors=4,
            net_version=4
        )
        embedding2 = get_embedding(
            screen=data[i]['post_xml'],
            ui_model='../model/UI2Vec_model.ep120',
            screen_model='../model/Screen2Vec_model_v4.ep120',
            layout_model='../model/layout_encoder.ep800',
            num_predictors=4,
            net_version=4
        )
        print(data[i]['description'], 'pre_xml similarity with post_xml')
        print(torch.cosine_similarity(embedding1, embedding2, dim=0))
        print(data[i]['pre_xml'])
        print(data[i]['post_xml'])
    pass


